import React from 'react';

export default function PaymentDetails() {
    // Define CreateOrder as an async function
    const CreateOrder = async () => {
        try {
            const response = await fetch('http://your-backend-url/create-paypal-order.php', {
                method: 'POST', // Ensure POST is in quotes
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            const data = await response.json();

            return data.orderID; // returns the PayPal order ID from the PHP server.
        } catch (error) {
            console.error('Error creating PayPal order:', error);
        }
    };

    // You might want to call CreateOrder in an effect or an event
    const handlePayment = async () => {
        const orderID = await CreateOrder();
        console.log('Created Order ID:', orderID);
        // You can handle further payment processing here
    };

    return (
        <div className='payment-details' id="paypal-button-container">
            <button onClick={handlePayment}>Create PayPal Order</button>
            <div>
                {/* Render your PayPal button or additional details here */}
            </div>
        </div>
    );
}
