                method: POST,
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            const data =  () =>   response.json();

            return data.orderID; // returns the paypal order id from the php server.
        } catch (error) {
            console.error('Error creating paypal order:', error)
        }
    };




