import React, { useState } from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import axios from "axios";
export default function PaymentDetails() {
    const [paid, setPaidFor] = useState(false);
    const [error, setError] = useState(false);
    const [amount, setAmount] = useState(20); // Allowing custom amount

    const CreateOrder = async (amount, currency = "USD") => {
        try {
            const response = await fetch('https://learnreflects.com/Server/Payment.php', {
                method: 'POST,GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ amount, currency }),
            });

            const data = await response.json();
            return data.orderID; // Return the order ID from the backend
        } catch (error) {
            console.error('Error creating PayPal order:', error);
            setError(true);
        }
    };

    const handleApprove = async (data, actions) => {
        try {
            // Capture the order using the actions object
            const orderData = await actions.order.capture();
            console.log("Order successfully captured! Order Data: ", orderData);
            setPaidFor(true);
        } catch (error) {
            console.error('Error capturing order:', error);
            setError(true);
        }
    };

    const handleError = (err) => {
        console.log('Error: ', err);
        setError(true);
    };

    return (
        <div>

            <PayPalScriptProvider options={{ "client-id": "AQTrkWTmd3pfVlhl_bNhKbCyjeVh0Yf-O-fsWLPw0sRgrzWwdPOCx0o_YFKSmoFfxkdecE0ySjfxoWPt" }}>
                <div className='payment-details' id="paypal-button-container">
                    {paid ? (
                        <h2>Payment Successful!</h2>
                    ) : (
                        <>
                            <input
                                type="number"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                                placeholder="Enter Amount"
                            />
                            <PayPalButtons
                                createOrder={async (data, actions) => {
                                    const orderID = await CreateOrder(amount); // Use custom amount
                                    return orderID; // Return the created order ID
                                }}
                                onApprove={handleApprove}
                                onError={handleError}
                            />
                        </>
                    )}
                    {error && <p>An error occurred during the payment process. Please try again.</p>}
                </div>
            </PayPalScriptProvider>
        </div>
    );
}
