import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Css/Menubar.css';
import { useNavigate } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';

export default function MenuBar() {
    const navigate = useNavigate();

    // State for modal
    const [show, setShow] = useState(false);
    const [modalContent, setModalContent] = useState({ title: '', action: '' });

    const handleClose = () => setShow(false);
    const handleShow = (title, action) => {
        setModalContent({ title, action });
        setShow(true);
    };

    const handleNavigation = (link) => {
        window.open(link, '_blank');
    };

    return (
        <div className='MenuBar-Container'>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container">
                    <div className="row">
                        {[
                            { title: 'Whitelist', action },
                            { title: 'LearnReflect AI - GPT Assistant', action: 'info about AI Assistant' },
                            { title: 'AI Audio Enhancer', action: 'info about Audio Enhancer' },
                            { title: 'AI Video Enhancer', action: 'info about Video Enhancer' },
                            { title: 'Contact', action: 'https://example.com/contact' }
                        ].map((item, index) => (
                            <div className="col-12 mb-3" key={index}>
                                <span
                                    onClick={() => {
                                        if (item.action.startsWith('http')) {
                                            handleNavigation(item.action); // For external links
                                        } else {
                                            handleShow(item.title, item.action); // For modal content
                                        }
                                    }}
                                    className="title-span">
                                    {item.title}
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
            </nav>

            {/* Modal for displaying information */}
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{modalContent.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {modalContent.action}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}
