import React, { useState } from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import axios from "axios";

export default function PaymentDetails() {
    const [paid, setPaidFor] = useState(false);
    const [error, setError] = useState(false);
    const [amount, setAmount] = useState(20); // Custom amount

    // Create order function
    const CreateOrder = async (amount, currency = "USD") => {
        try {
            // Make a GET request to fetch order data from the backend
            const response = await axios.get(`https://learnreflects.com/Server/Payment.php?amount=${amount}&currency=${currency}`);
            console.log(response.data); // Log the response data
            return response.data.orderID; // Return the order ID from the backend
        } catch (error) {
            console.error('Error creating PayPal order:', error);
            setError(true); // Set error state if there's an issue
        }
    };

    // Handle order approval (when payment is completed)
    const handleApprove = async (data, actions) => {
        try {
            // Capture the order using the actions object
            const orderData = await actions.order.capture();
            console.log("Order successfully captured! Order Data: ", orderData);
            setPaidFor(true); // Set payment success state
        } catch (error) {
            console.error('Error capturing order:', error);
            setError(true); // Set error state if there's an issue
        }
    };

    // Handle errors in payment process
    const handleError = (err) => {
        console.log('Error: ', err);
        setError(true); // Set error state
    };

    return (
        <div>
            <PayPalScriptProvider options={{ "client-id": "AQTrkWTmd3pfVlhl_bNhKbCyjeVh0Yf-O-fsWLPw0sRgrzWwdPOCx0o_YFKSmoFfxkdecE0ySjfxoWPt" }}>
                <div className='payment-details' id="paypal-button-container">
                    {paid ? (
                        <h2>Payment Successful!</h2>
                    ) : (
                        <>
                            <input
                                type="number"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                                placeholder="Enter Amount"
                            />
                            <PayPalButtons
                                createOrder={async (data, actions) => {
                                    const orderID = await CreateOrder(amount); // Use the custom amount
                                    return orderID; // Return the created order ID
                                }}
                                onApprove={handleApprove} // Approve the order
                                onError={handleError} // Handle any errors
                            />
                        </>
                    )}
                    {error && <p>An error occurred during the payment process. Please try again.</p>}
                </div>
            </PayPalScriptProvider>
        </div>
    );
}
