import React, { useState } from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
export default function paymentdetails() {
    const [paid, setPaidFor] = useState(false);
    const [error, setError] = useState(false);
    

    const CreateOrder = async () => {
        try {
            const response = await fetch('http://your-backend-url/create-paypal-order.php', {
                method: POST,
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            const data = await  response.json();

            return data.orderID; // returns the paypal order id from the php server.
        } catch (error) {
            console.error('Error creating paypal order:', error)
        }
    };


    const handlePayment = async () => {
        const orderID = await CreateOrder();
        console.log('Created Order ID: ', orderID);

    }

    const handleApprove = async (orderID) => {
        console.log("Order successfully captured!".orderID);
        setPaidFor(true);
     }


     const handleerror = async (err) => {
        console.log('Error: ',err)
        setError(err);
     }

    return (
        <PayPalScriptProvider options={{ "client-id":""}}>
        <div className='payment-details' id="paypal-button-container">
            { paid ? (
                <h2>FUCK YOU</h2>
            ) : ( 
                <PayPalButtons CreateOrder={(data,actions) => {
                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                value: "100.00"
                            }
                        }]
                    });
                }}>

                </PayPalButtons>
            )}
        </div>
        </PayPalScriptProvider>
    )



}
