import React, { useState } from 'react';
import './Css/Menubar.css';
import { useNavigate } from 'react-router-dom';
import contactimg from './Images/contact.png';
import whitelistimg from './Images/whitelist.png';
import DescriptionComponent from './DescriptionComponent';

export default function MenuBar() {
    const navigate = useNavigate();
    const [activesection, setActiveSection] = useState('');
    const [imagesize, setimagesize] = useState({
        height: {
            whitelist: 5,
            contact: 75,
        },
        width: {
            whitelist: 10,
            contact: 75,
        },
    });

    const handletoggle = (section) => {
        if (activesection === section) {
            setActiveSection(''); // Close the section if it's already open
        } else {
            setActiveSection(section); // Open the new section
        }
    };

    const isSectionActive = (section) => activesection === section;

    return (
        <div className='menubar'>
            <div className='Menubar-list'>
                <ul
                    style={{
                        backgroundImage: `url(${whitelistimg})`,
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        backgroundRepeat: 'no-repeat',
                        height: `${imagesize.height.whitelist}`,
                        width: `${imagesize.width.whitelist}`,
                    }}
                    className='Whitelist'
                    onClick={() => navigate('/PageComponent')}
                ></ul>

                <ul
                    onClick={() => handletoggle('VideoUpscaler')}
                    className={isSectionActive('VideoUpscaler') ? 'disabled' : ''}
                >
                    LearnReflect's AI Videoupscaler
                    {isSectionActive('VideoUpscaler') && (
                        <DescriptionComponent
                            title='Video Upscaler'
                            description='LearnReflects AI Video Upscaler is a powerful tool designed to elevate your video content to new levels of clarity and detail. Using advanced AI algorithms, the Video Upscaler enhances video quality by improving resolution, reducing noise, and refining visual details in your footage. Whether you’re working with low-resolution videos or simply want to enhance the visual quality of your content, the Video Upscaler transforms your videos into crisp high-definition formats. This technology is perfect for content creators, video professionals, or anyone who wants their visual content to stand out. The Video Upscaler works seamlessly alongside LearnReflects suite of AI-driven tools for personal growth, productivity, and self-improvement, ensuring that you not only improve yourself but also the quality of your work.'
                            onClose={() => setActiveSection('')}
                        />
                    )}
                </ul>

                <ul
                    onClick={() => handletoggle('AudioUpscaler')}
                    className={isSectionActive('AudioUpscaler') ? 'disabled' : ''}
                >
                    LearnReflect's AI AudioUpscaler
                    {isSectionActive('AudioUpscaler') && (
                        <DescriptionComponent
                            title='Audio Upscaler'
                            description='Audio Upscaling Software. Our upscaling program, Audio Enc, is designed to elevate your audio experience to new heights. With advanced sound enhancement algorithms, Audio Enc enables you to convert and improve the quality of music, podcasts, and other audio content. The software supports multiple file formats and delivers crystal-clear sound, providing listeners with a richer and more immersive experience. Whether you are a professional audio engineer or a music enthusiast, Audio Enc will help you achieve optimal sound quality with ease and efficiency.'
                            onClose={() => setActiveSection('')}
                        />
                    )}
                </ul>

                <ul
                    onClick={() => handletoggle('description')}
                    className={isSectionActive('description') ? 'disabled' : ''}
                >
                    LearnReflect's AI Chatbot
                    {isSectionActive('description') && (
                        <DescriptionComponent
                            title='LearnReflects AI Chatbot'
                            description='LR-Chatbot: Your Personalized Self-Improvement Assistant. Introducing the LR-Chatbot, an integral component of the LearnReflect self-improvement platform, designed to empower your personal growth journey. This AI-driven chatbot leverages advanced machine learning techniques to provide tailored guidance and support as you work towards achieving your goals. Pre-trained specifically for self-improvement, the LR-Chatbot engages in meaningful conversations, continuously learning from your interactions to adapt to your unique aspirations. Whether you’re striving to build discipline, enhance motivation, or develop effective daily routines, this chatbot becomes a personal companion dedicated to your success. The LR-Chatbot offers personalized discipline-building strategies and motivation techniques, ensuring that you remain focused and accountable on your journey. With its ability to understand your evolving needs, the chatbot provides insights and encouragement that resonate with you, making self-improvement an achievable goal. In conjunction with our advanced AI models for enhancing video and audio quality, LearnReflect serves as an all-in-one solution for anyone committed to self-improvement and productivity.'
                            onClose={() => setActiveSection('')}
                        />
                    )}
                </ul>

                <ul
                    onClick={() => navigate('./ContactNavBar')}
                    style={{
                        backgroundImage: `url(${contactimg})`,
                        height: `${imagesize.height.contact}px`,
                        width: `${imagesize.width.contact}`,
                        backgroundSize: 'cover',
                    }}
                    className={isSectionActive('contact') ? 'disabled' : ''}
                >
                    {isSectionActive('contact') && <li></li>}
                </ul>
            </div>
        </div>
    );
}
